import React, { useState } from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Slider from '@mui/material/Slider';
import Button from '@mui/material/Button';

const defaultWeights = {
  safety: 25,
  pollution: 25,
  cleanliness: 25,
  greenery: 25,
};

export default function MatchForm({ onMatch }) {
  const [weights, setWeights] = useState(defaultWeights);
  const [loading, setLoading] = useState(false);

  const handleChange = (name) => (e, value) => {
    setWeights((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    const res = await fetch('http://localhost:5000/api/match', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(weights),
    });
    const data = await res.json();
    setLoading(false);
    onMatch(data);
  };

  return (
    <Box component="form" onSubmit={handleSubmit} sx={{ mt: 4, mb: 4 }}>
      <Typography variant="h5" gutterBottom>Set Your Preferences</Typography>
      {Object.keys(weights).map((key) => (
        <Box key={key} sx={{ mb: 2 }}>
          <Typography gutterBottom>{key.charAt(0).toUpperCase() + key.slice(1)}</Typography>
          <Slider
            value={weights[key]}
            onChange={handleChange(key)}
            min={0}
            max={100}
            step={1}
            valueLabelDisplay="auto"
          />
        </Box>
      ))}
      <Button type="submit" variant="contained" disabled={loading}>
        {loading ? 'Matching...' : 'Find My Neighborhoods'}
      </Button>
    </Box>
  );
}
